function openBar(){let e=document.getElementsByClassName("content")[0];document.getElementsByClassName("menu-toggle")[0].children[0].checked?(document.getElementsByClassName("nav-links")[0].style.transform="none",e.style.opacity=.38,e.style.background="#333333"):(document.getElementsByClassName("nav-links")[0].style.transform="translate(0,-200%)",e.style.opacity=1,e.style.background="none")}document.addEventListener("DOMContentLoaded",(function(){document.getElementsByClassName("menu-toggle")[0].children[0].addEventListener("change",openBar)}));